<script>
// require(".././assets/css/dilipcss.css");
// import Navbar from "../components/Navbar";
// import Footer from "../components/Footer";
import axios from "axios";
//import TextField from "../components/TextField"
export default {
  name: "ProductDetail",
  components: {
    // Navbar,
    // Footer,
    //TextField
  },
  data() {
    return {
      ProductData: [],
      uid: localStorage.getItem("user"),
      classActive: 0,
      classNone: 0,
      addressDatas: [],
      stateData: [],
      chkadd: false,

      ip_address: JSON.parse(localStorage.getItem("ip_address")),
      uname: localStorage.getItem("username"),
      cartcount: 0,
      total: 0,
      pmode: "",

      username: "",
      password: "",

      cartData: [],
      selectedAddress: "",
      aid: 0,
      address_box: true,

      address_name: "",
      address_mobile: "",
      add1: "",
      add2: "",
      address_state: "",
      address_city: "",
      pincode: "",
    };
  },
  mounted() {
    // console.log(this.$gmapOptions.load.key)
    this.getData();
    this.getAddress();
    this.getState();
    this.getSelectedAddress();
    this.mycartData();
    if (this.uid != null) {
      this.clsActive(2);
    } else {
      this.clsActive(1);
    }
  },
  methods: {
    getData() {
      var self = this;
      axios({
        method: "POST",
        url: this.$root.URL_ROOT + "api.php",
        data: { type: "productDetails", id: "3" },
      }).then(function (response) {
        var data = response.data;
        self.ProductData = data;
        console.log("mkmk");
        console.log(data);
      });
    },
    login() {
      var vi = this;
      if (this.username == "") {
        this.$toasted.global.error({ message: "Please enter Email" });
      } else if (this.password == "") {
        this.$toasted.global.error({ message: "Please enter Password" });
      } else {
        axios({
          method: "POST",
          url: this.$root.URL_ROOT + "api.php",
          data: {
            type: "login",
            username: this.username,
            password: this.password,
            ip_address: this.ip_address,
          },
        }).then(function (response) {
          var data = response.data;

          if (data.status === "success") {
            vi.token = data.body.access_token;
            vi.role = data.body.isAdmin;
            localStorage.setItem("user", data.body.id);
            localStorage.setItem("username", data.body.username);

            localStorage.setItem("token", data.body.access_token);
            localStorage.setItem("isAdmin", data.body.role);
            vi.$router.go("/");
            vi.boxchk = false;
            vi.$toasted.global.success({ message: "Login Successfull..." });
          } else {
            vi.$toasted.global.error({ message: "Invalid id or password" });
          }
        });
      }
    },
    clsActive(id) {
      this.classActive = id;
      this.classNone = id;

      if (id == 2) {
        this.address_box = true;
      } else {
        // this.address_box=false;
      }
    },
    getAddress() {
      var ev = this;
      axios({
        method: "POST",
        url: this.$root.URL_ROOT + "api.php",
        data: {
          type: "getAddress",
          uid: ev.uid,
        },
      }).then(function (response) {
        var data = response.data;
        if (data.status == "success") {
          ev.addressDatas = data.data;
          ev.chkadd = true;
        } else {
          ev.chkadd = false;
        }
      });
    },
    save_address() {
      var ev = this;

      if (this.address_name == "") {
        ev.$toasted.global.error({ message: "please enter address title " });
      } else if (this.address_mobile == "") {
        ev.$toasted.global.error({ message: "please enter mobile number " });
      } else if (this.add1 == "") {
        ev.$toasted.global.error({ message: "please enter address 1 " });
      } else if (this.add2 == "") {
        ev.$toasted.global.error({ message: "please enter address 2 " });
      } else if (this.address_city == "") {
        ev.$toasted.global.error({ message: "please enter city " });
      } else if (this.address_state == "") {
        ev.$toasted.global.error({ message: "please select state " });
      } else {
        axios({
          method: "POST",
          url: this.$root.URL_ROOT + "api.php",
          data: {
            type: "save_address",
            uid: ev.uid,
            address_name: ev.address_name,
            address_mobile: ev.address_mobile,
            add1: ev.add1,
            add2: ev.add2,
            pincode: ev.pincode,
            address_state: ev.address_state,
          },
        }).then(function (response) {
          var data = response.data;
          //ev.stateData=data
          console.log(response);
          if (data.status == "success") {
            ev.$toasted.global.success({ message: data.msg });
            ev.$refs.fileInput.click();
            ev.getAddress();
          }
        });
      }
    },
    getState() {
      var ev = this;
      axios({
        method: "POST",
        url: this.$root.URL_ROOT + "api.php",
        data: {
          type: "getState",
        },
      }).then(function (response) {
        var data = response.data;
        ev.stateData = data;
      });
    },
    getSelectedAddress() {
      var ev = this;
      axios({
        method: "POST",
        url: this.$root.URL_ROOT + "api.php",
        data: {
          type: "getSelectedAddress",
          uid: ev.uid,
          aid: ev.aid,
        },
      }).then(function (response) {
        var data = response.data;
        ev.selectedAddress = data;
      });
    },
    checked_add(id) {
      this.aid = id;
      this.getSelectedAddress();
      this.address_box = true;
    },
    chng_add_box() {
      this.address_box = false;
    },
    mycartData() {
      var self = this;
      axios({
        method: "POST",
        url: this.$root.URL_ROOT + "api.php",
        data: {
          type: "mycartdata",
          uid: this.uid,
          ip_address: this.ip_address,
        },
      }).then(function (response) {
        var data = response.data;
        self.cartData = data.mycartdata;
        self.cartcount = data.cart_count;
        self.total = data.total;
        //console.log("mycartdata");
        console.log(self.cartcount);
        console.log("dededeed");
      });
    },

    update_cart_pre(cid, pid, pname, qty, price, mode) {
      var t = this.update_cart(cid, pid, pname, qty, price, mode);
      // console.log(t)
      setTimeout(() => {
        if (t != null) {
          this.mycartData();
        }
      }, 1000);
    },
    setPyment(pmode) {
      this.pmode = pmode;
    },
    place_order() {
      var ev = this;

      if (this.uid == "" || this.uid == null) {
        ev.$toasted.global.error({ message: "please login first" });
      } else if (this.aid == 0) {
        ev.$toasted.global.error({ message: "please select address " });
      } else if (this.pmode == "") {
        ev.$toasted.global.error({ message: "please select payment methode " });
      } else {
        axios({
          method: "POST",
          url: this.$root.URL_ROOT + "api.php",
          data: {
            type: "placeorder",
            uid: this.uid,
            aid: this.aid,
            ip_address: this.ip_address,
          },
        }).then(function (response) {
          var data = response.data;
          if (data.status == "success") {
            location.href =
              ev.$root.URL_ROOT + "payment.php?pay_title=order&oid=" + data.oid;
            // ev.$toasted.global.success({ message: 'Order Place Successfully ' })
            //this.$router.push('/product')
            //window.location = ev.$root.Home_link;
          }
          console.log("place");
          console.log(data);
        });
      }
    },
  },
};
</script>
